module.exports = {
  tags: ['masterhora'],
  'Demo test Masterhora_login' : function (client) {
    client
      .url('http://masterhora.de/')
	  .waitForElementVisible('body', 1000)
      .assert.title('MASTERhora')
	  .click(".exposed > a") //click on "Einloggen"
	  .waitForElementVisible("#modal-login", 1000) //wait for modal window
	  .setValue('input[name=email]', 'cantehnik@gmail.com')
	  .setValue('input[name=password]', 'qwe123')
	  .click('button[name=login]')
	  .waitForElementVisible('body', 1000)
	  .assert.containsText(".media-title", 'Gerd Mueller') //username in the right up corner is visible
	  .assert.containsText("ul.nav:nth-child(2) > li:nth-child(5) > a", 'Abmelden') //logout link exists
	  .click("ul.nav:nth-child(2) > li:nth-child(5) > a") //click logout
	  .waitForElementVisible(".exposed > a", 1000)
	  .assert.containsText(".exposed > a", 'Einloggen')
      .end();
  }
};